mapper.insert:
INSERT INTO SHOP_CART (
     BOOK_NUM
    , CART_CNT
    , MEM_ID
    , TOTAL_PRICE
) VALUES (
     #{bookNum}
    , #{cartCnt}
    , #{memId}
    , (SELECT PRICE
        FROM BOOK
        WHERE BOOK_NUM = #{bookNum}) * #{cartCnt}
);

mapper.select:
SELECT MEM_ID
FROM SHOP_CART
WHERE BOOK_NUM = #{bookNum}
AND MEM_ID = #{memId}

mapper.update:
UPDATE shop_cart
SET
    CART_CNT = CART_CNT + #{cartCnt}
    , TOTAL_PRICE = (SELECT PRICE
                     FROM BOOK
                     WHERE BOOK_NUM = #{bookNum}) * CART_CNT
WHERE MEM_ID = #{memId}
AND BOOK_NUM = #{bookNum}

UPDATE SHOP_CART
SET
    CART_CNT = #{cartCnt}
    , TOTAL_PRICE = (SELECT PRICE
                    FROM BOOK
                    WHERE BOOK_NUM = #{bookNum}) * #{cartCnt}
WHERE CART_NUM = #{cartNum}
